import Header from './Header';

import 'bootstrap/dist/css/bootstrap.min.css';

const mainLayout = {
    //backgroundColor: 'ghostwhite'
    overflowY: 'hidden',
    height: '100%'
};

const subLayout = {
    textAlign: 'center',
    float: 'center',
    marginLeft: 'auto',
    marginRight: 'auto',
    width: '82.5%',
    height: '100%',
    overflowY: 'hidden'
};

export default (props) => (
    <div style={mainLayout} >
     {/* <div> */}
        <div style={subLayout}>
    	    <Header />
    	    {props.children}
  	    </div>
    </div>
);
