import Layout from '../components/Layout.js';
import Link from 'next/link';
import fetch from 'isomorphic-unfetch';

import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Image from 'react-bootstrap/Image';
import ButtonGroup from 'react-bootstrap/ButtonGroup';
import Dropdown from 'react-bootstrap/Dropdown';
import DropdownButton from 'react-bootstrap/DropdownButton';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHome } from '@fortawesome/free-solid-svg-icons';

// let globalUserInput;
//color: name.toString().indexOf(userInput) >= 0 ? 'red' : 'yellow'

const Index = (props) => (
    <Layout>
        <div>

            <hr style={{ backgroundColor: 'black', padding: '0.75px', marginTop: 0, marginBottom: 0 }} ></hr>
            <h2 style={{ marginBottom: 0 }} > <FontAwesomeIcon icon={faHome} /> HOME</h2>
            <DropdownButton as={ButtonGroup} title="Dropdown" id="bg-nested-dropdown">
                <Dropdown.Item eventKey="1">Dropdown link</Dropdown.Item>
                <Dropdown.Item eventKey="2">Dropdown link</Dropdown.Item>
            </DropdownButton>
            <hr style={{ backgroundColor: 'black', padding: '0.75px', marginTop: 0, marginBottom: '25px' }} ></hr>

            <Container fluid style={{ width: '1350px', float: 'center', textAlign: 'center', marginleft: 'auto', marginRight: 'auto' }} >
                <Row>
                    {props.countryData.map(({ alpha2Code, name, capital, flag, region, subregion, latlng, area, population, timezones, demonym, callingCodes }) => (
                        <div className='col-md-3' >
                            <Link as={`/${alpha2Code}`} href={`/selectedCountry?alpha2Code=${alpha2Code}&name=${name}&capital=${capital}&flag=${flag}&continent=${region}&region=${subregion}&lat=${latlng[0]}&lon=${latlng[1]}&area=${area}&population=${population}&timezone=${timezones[0]}&demonym=${demonym}&callingCode=${callingCodes[0]}`} >
                                <a style={{ fontSize: '11px', color: 'black' , textDecoration: 'none', float: 'left', textAlign: 'left' }} > <Image src={flag} style={{ width: '7.5%', marginBottom: '4px', marginRight: '5px', float: 'left', textAlign: 'left' }} />{name}</a>
                            </Link>
                            <br></br>
                            <br></br>
                        </div> 
                    ))} 
                </Row>
            </Container>

        </div>
    </Layout>
);

Index.getInitialProps = async function() {

    const result = await fetch('https://restcountries.eu/rest/v2/all');
    const data = await result.json();

    console.log(`Country Data Successfully Fetched! Length of Data is: ${data.length}`);
    console.log(`  `);
    console.log(`Country Data: ${JSON.stringify(data)}`);

    return {
        countryData: data
    };

};

