import Header from './Header';

import 'bootstrap/dist/css/bootstrap.min.css';

// const mainLayout = {
//     backgroundColor: 'ghostwhite'
// };

const subLayout = {
    textAlign: 'center',
    float: 'center',
    marginLeft: 'auto',
    marginRight: 'auto',
    width: '75%',
};

export default (props) => (
    // <div style={mainLayout} >
    <div>
        <div style={subLayout}>
    	    <Header />
            <br></br>
            <h1 style={{ marginBottom: '0px' }} >REST COUNTRIES API</h1>
            <hr style={{ width: '20rem', backgroundColor: 'black', padding: '1px', marginTop: '0px' }} ></hr>
    	    {props.children}
  	    </div>
    </div>
);
