import { withRouter } from 'next/router';

import Layout from '../components/Layout.js';

import Image from 'react-bootstrap/Image';

const SelectedCountry = withRouter(props => (
    <div>
        <hr style={{ backgroundColor: 'black', padding: '0.75px', marginTop: 0, marginBottom: 0 }} ></hr>
        <h2 style={{ marginBottom: 0 }} > <Image src={props.router.query.flag} style={{ width: '2.75%', marginBottom: '4px' }} /> {(props.router.query.name).toUpperCase()}</h2>
        <hr style={{ backgroundColor: 'black', padding: '0.75px', marginTop: 0, marginBottom: '25px' }} ></hr>

        <Row>
            <Col>
                <h4><b>Capital:</b></h4>
            </Col>
        </Row>

        <h4>Capital: {props.router.query.capital}</h4>

    </div>
));

export default () => (
    <Layout>
        <div>
            <SelectedCountry />
        </div>
    </Layout>
);

