import { withRouter } from 'next/router';

import Layout from '../components/Layout.js';

import Image from 'react-bootstrap/Image';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';

const titleStyle = {
    textAlign: 'right',
    float: 'right'
};

const valueStyle = {
    textAlign: 'left',
    float: 'left'
};

const SelectedCountry = withRouter(props => (
    <div>
        <hr style={{ backgroundColor: 'black', padding: '0.75px', marginTop: 0, marginBottom: 0 }} ></hr>
        <h2 style={{ marginBottom: 0 }} > <Image src={props.router.query.flag} style={{ width: '2.75%', marginBottom: '4px' }} /> {(props.router.query.name).toUpperCase()}</h2>
        <hr style={{ backgroundColor: 'black', padding: '0.75px', marginTop: 0, marginBottom: '25px' }} ></hr>

        <Card style={{ width: '25rem' }}>
            <Image src={props.router.query.flag} style={{ width: '95%', textAlign: 'center', float: 'center', marginLeft: 'auto', marginRight: 'auto' }} />
            <Card.Body>
                        <Row>
            <Col>
                <h4 style={titleStyle} ><b>Capital:</b></h4>
            </Col>
            <Col>
                <h4 style={valueStyle} >{props.router.query.capital}</h4>
            </Col>
        </Row>
        <Row>
            <Col>
                <h4 style={titleStyle} ><b>Region:</b></h4>
            </Col>
            <Col>
                <h4 style={valueStyle} >{props.router.query.region}</h4>
            </Col>
        </Row>
        <Row>
            <Col>
                <h4 style={titleStyle} ><b>Continent:</b></h4>
            </Col>
            <Col>
                <h4 style={valueStyle} >{props.router.query.continent}</h4>
            </Col>
        </Row>
            </Card.Body>
        </Card>

        {/* <Row>
            <Col>
                <h4 style={titleStyle} ><b>Capital:</b></h4>
            </Col>
            <Col>
                <h4 style={valueStyle} >{props.router.query.capital}</h4>
            </Col>
        </Row>
        <Row>
            <Col>
                <h4 style={titleStyle} ><b>Region:</b></h4>
            </Col>
            <Col>
                <h4 style={valueStyle} >{props.router.query.region}</h4>
            </Col>
        </Row>
        <Row>
            <Col>
                <h4 style={titleStyle} ><b>Continent:</b></h4>
            </Col>
            <Col>
                <h4 style={valueStyle} >{props.router.query.continent}</h4>
            </Col>
        </Row> */}

        {/* <h4>Capital: {props.router.query.capital}</h4> */}

    </div>
));

export default () => (
    <Layout>
        <div>
            <SelectedCountry />
        </div>
    </Layout>
);

